import React, { Component } from 'react';

class Write extends Component {
    state = {
        name: '',
        hospital: '',
        data: '',
    }

    handleChange = (e) => {
        this.setState({
            [e.target.name]: e.target.value
        })
    }

    render() {
        return (
          <form>
            <input
              placeholder="이름"
              value={this.state.name}
              onChange={this.handleChange}
              name="name"
            />
            <input
              placeholder="병원"
              value={this.state.hospital}
              onChange={this.handleChange}
              name="hospital"
            />
            <input
              placeholder="데이터"
              value={this.state.data}
              onChange={this.handleChange}
              name="data"
            />
            <div>{this.state.name} {this.state.hospital} {this.state.data}</div>
          </form>
        );
    }

}

export default Write;
